import React, { useCallback, useState } from 'react';
import { useDropzone } from 'react-dropzone';

const Main = () => {
  const [files, setFiles] = useState([]);

  const onDrop = useCallback(acceptedFiles => {
    setFiles(acceptedFiles.map(file => ({
      name: file.name,
      path: file.path
    })));
  }, []);

  const { getRootProps, getInputProps } = useDropzone({
    onDrop,
    accept: 'application/pdf'
  });

  return (
    <div style={styles.container}>
      <header style={styles.header}>
        <h1 style={styles.title}>AcquiSolar</h1>
        <a href="/about" style={styles.aboutLink}>About Us</a>
      </header>
      <div style={styles.content}>
        <div {...getRootProps({ style: styles.dropzone })}>
          <input {...getInputProps()} />
          <p style={styles.uploadText}>Upload Files</p>
          <div style={styles.fileListContainer}>
            <p>Files:</p>
            <ul style={styles.fileList}>
              {files.map(file => (
                <li key={file.path} style={styles.fileName}>{file.name}</li>
              ))}
            </ul>
          </div>
        </div>
        <button style={styles.analyzeButton}>Analyze</button>
      </div>
    </div>
  );
};

const styles = {
  container: {
    fontFamily: 'Arial, sans-serif',
    height: '100vh',
    backgroundColor: '#9ACAC4',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'flex-start', // Align to the left
  },
  header: {
    width: '100%', // Ensure the header takes full width
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    padding: '20px',
    backgroundColor: '#9ACAC4',
    color: 'white',
    fontSize: '24px',
  },
  content: {
    display: 'flex',
    flexDirection: 'column',
    padding: '20px',
    alignItems: 'flex-start', // Align content to the left
  },
  dropzone: {
    padding: '20px',
    width: '280px',
    height: '400px',
    borderWidth: '2px',
    borderRadius: '20px',
    borderColor: '#cccccc',
    borderStyle: 'dashed',
    backgroundColor: '#ffffff',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center',
    marginBottom: '10px',
  },
  uploadText: {
    color: '#000000',
    marginBottom: '20px',
    fontSize: '18px',
    fontWeight: 'bold',
  },
  fileListContainer: {
    width: '90%',
    backgroundColor: '#E6F0EF', // Light green background for file list
    borderRadius: '10px',
    padding: '10px',
    textAlign: 'left',
    maxHeight: '200px', // Set a max height
    overflowY: 'auto', // Enable scroll if the content overflows
  },
  fileList: {
    listStyleType: 'none',
    padding: 0,
    margin: 0,
  },
  fileName: {
    color: '#000000', // Black text for file names
    fontSize: '16px',
    lineHeight: '24px',
    padding: '5px 0', // Add some padding to each file name
  },
  analyzeButton: {
    padding: '10px 20px',
    fontSize: '16px',
    backgroundColor: '#4CAF50',
    border: 'none',
    borderRadius: '20px',
    cursor: 'pointer',
    color: 'white',
  },
  aboutLink: {
    color: 'white',
    textDecoration: 'none',
    margin: "10%",
  },
};

export default Main;
