import React, { useCallback, useState } from 'react';
import { useDropzone } from 'react-dropzone';

import { Document, Page, pdfjs } from 'react-pdf';

pdfjs.GlobalWorkerOptions.workerSrc = `//cdnjs.cloudflare.com/ajax/libs/pdf.js/${pdfjs.version}/pdf.worker.js`;

// url of aws server and port 80
// change to '' for localhost
// const URL = 'http://54.90.226.66:80'
const URL = ''

const Main = () => {
  const [files, setFiles] = useState([]);
  const [fileNames, setFileNames] = useState([]);
  const [showSearch, setShowSearch] = useState(false);
  const [searchQuery, setSearchQuery] = useState('');
  const [currentFile, setCurrentFile] = useState(null); // State to hold the currently selected PDF for viewing
  const [numPages, setNumPages] = useState(null); // Number of pages in the current PDF
  const [pageNumber, setPageNumber] = useState(1); // Current page number
  const [instances, setInstances] = useState([]); // Instances of the search term
  const [currentInstance, setCurrentInstance] = useState(0); // Current instance of the search term

  const onDocumentLoadSuccess = ({ numPages }) => {
    setNumPages(numPages);
    // Optionally start search automatically
  };

    // Function to open a PDF for viewing and searching
    const openFile = (file) => {
      setCurrentFile(file);
      setPageNumber(1);
    };
    // Function to search within the current PDF
    const searchCurrentFile = async () => {
      let instancesFound = [];
      if (!currentFile) return;

      const pdfDocument = await pdfjs.getDocument(URL.createObjectURL(currentFile)).promise;

      for (let i = 1; i <= numPages; i++) {
        const page = await pdfDocument.getPage(i);
        const textContent = await page.getTextContent();
        textContent.items.forEach((item) => {
          if (item.str.includes(searchQuery)) {
            instancesFound.push({ page: i, instance: item.str });
          }
        });
      }
      setInstances(instancesFound);
      setCurrentInstance(0); // Reset to the first instance
      if (instancesFound.length > 0) {
        setPageNumber(instancesFound[0].page); // Go to the first instance page
      }
    };

    // Function to navigate to the next instance of the search term
    const goToNextInstance = () => {
      if (instances.length === 0) return;
      const nextInstance = (currentInstance + 1) % instances.length;
      setCurrentInstance(nextInstance);
      setPageNumber(instances[nextInstance].page);
    };

  const handleSearchInputChange = (event) => {
    setSearchQuery(event.target.value);
  };

  const handleSearchSubmit = async () => {
    if (!searchQuery) {
      alert('Please enter a search query.');
      return;
    }

    try {
      const response = await fetch(URL + '/search', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ query: searchQuery }),
      });

      if (response.ok) {
        const data = await response.json();
        console.log(data);
      } else {
        console.error('Search failed', response);
      }
    } catch (error) {
      console.error('Error during search', error);
    }
  };

  const onDrop = useCallback((acceptedFiles) => {
    // Prevents duplicates from being uploaded (based on filename atm)
    const filteredFiles = acceptedFiles.filter(file => file.type === 'application/pdf');
    setFiles((prevFiles) => [...prevFiles, ...filteredFiles]);

    // Filenames that are displayed when file is uploaded
    setFileNames((prevFiles) => {
      const newFileNames = filteredFiles.map((file) => file.name);
      const existingFileNames = new Set(prevFiles);
      const uniqueNewFileNames = newFileNames.filter((fileName) => !existingFileNames.has(fileName));
      return [...prevFiles, ...uniqueNewFileNames];
    });
  }, []);

  const removeFile = (fileName, event) => {
    event.stopPropagation(); // Prevent the event from bubbling up to parent elements

    setFiles((prevFiles) => prevFiles.filter((file) => file.name !== fileName));
    setFileNames((prevFileNames) => prevFileNames.filter((name) => name !== fileName));
  };

  const { getRootProps, getInputProps } = useDropzone({
    // Only allows pdfs
    accept: 'application/pdf',
    onDrop,
  });

  const uploadFilesToServer = async () => {
    if (files.length === 0) {
      alert('Please submit a file.');
      return;
    }

    setShowSearch(true); 
    const formData = new FormData();

    // Append each file to the form data
    files.forEach((file) => {
      formData.append('files', file);
    });

    console.log(formData);

    try {
      const response = await fetch(URL + '/upload', {
        method: 'POST',
        body: formData,
      });

      if (response.ok) {
        console.log('Files successfully uploaded');
      } else {
        console.error('Upload failed', response);
      }
    } catch (error) {
      console.error('Error uploading files', error);
    }
  };

  return (
<div style={styles.container}>
  <header style={styles.header}>
    <h1 style={styles.title}>AcquiSolar</h1>
    <a href="/about" style={styles.aboutLink}>About Us</a>
  </header>

  {showSearch && (
  <div style={styles.searchContainer}>
    <input 
      type="text" 
      placeholder="Search..." 
      style={styles.searchInput} 
      value={searchQuery} 
      onChange={handleSearchInputChange} 
    />
    <button style={styles.searchButton} onClick={handleSearchSubmit}>Search</button>
  </div>
)}

  <div style={styles.mainContent}>
    <div {...getRootProps({ style: styles.dropzone })}>
      <input {...getInputProps()} />
      <div style={styles.uploadHeader}>
        <p style={styles.uploadText}>Upload Files</p>
        <button style={styles.addButton}>Add</button>
      </div>
      <div style={styles.fileListContainer}>
        <p style={styles.filesTitle}>Files:</p>
        <ul style={styles.fileList}>
          {fileNames.map((fileName, index) => (
            <li key={index} style={styles.fileName}>
              {fileName}
              <button 
                style={styles.removeButton} 
                onClick={(event) => removeFile(fileName, event)}
              >
                âœ•
              </button>
            </li>
          ))}
        </ul>
      </div> 
    </div>
    {!showSearch && (
      <div style={styles.dragTextContainer}>
        <p style={styles.dragText}>Add your files in the box on the left and click Submit.</p>
      </div>
    )}
  </div>

  {!showSearch && (
    <button style={styles.analyzeButton} onClick={uploadFilesToServer}>Submit</button>
  )}
        {currentFile && (
        <div>
          <Document file={currentFile} onLoadSuccess={onDocumentLoadSuccess}>
            <Page pageNumber={pageNumber} />
          </Document>
          <input
            type="search"
            value={searchQuery}
            onChange={handleSearchInputChange}
          />
          <button onClick={searchCurrentFile}>Search</button>
          {instances.length > 0 && (
            <button onClick={goToNextInstance}>
              Next Instance ({currentInstance + 1}/{instances.length})
            </button>
          )}
        </div>
      )}
</div>
  );
};

const styles = {
  container: {
    fontFamily: 'Arial, sans-serif',
    height: '100vh',
    width: '100%',
    backgroundColor: '#9ACAC4',
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'flex-start',
  },
  header: {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    padding: '20px',
    color: 'white',
    fontSize: '24px',
  },
  title: {
    margin: 0,
  },
  aboutLink: {
    color: 'white',
    textDecoration: 'none',
    backgroundColor: '#9ACAC4', 
    padding: '5px 10px',
    borderRadius: '5px', 
  },
  filesTitle: {
    margin: '8px', 
  },
  content: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'flex-start', 
    padding: '20px',
    paddingTop: '10px',
    width: '100%',
  },
  dropzone: {
    padding: '20px',
    width: '300px',
    height: '400px',
    borderRadius: '20px',
    backgroundColor: '#ffffff',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center',
    marginBottom: '10px',
  },
  uploadText: {
    color: '#000000',
    alignItems: 'flex-start',
    justifyContent: 'flex-start',
    marginBottom: '10px',
    fontSize: '18px',
    fontWeight: 'bold',
  },
  fileListContainer: {
    width: '100%',
    height: '100%',
    backgroundColor: '#E6F0EF',
    borderRadius: '10px',
    padding: '0px',
    textAlign: 'left',
    overflowY: 'auto', 
  },
  fileList: {
    listStyleType: 'none',
    padding: 0,
    margin: 0,
  },
  fileName: {
    color: '#000000',
    fontSize: '16px',
    lineHeight: '24px',
    margin: '8px',
  },
