import React, { useCallback, useState } from 'react';

const Main = () => {
  const [fileName, setFileName] = useState('');
  const [pdfText, setPdfText] = useState('');  // State to store the extracted text

  const handleFileUpload = async (file) => {
    const formData = new FormData();
    formData.append('pdf', file);

    try {
      const response = await fetch('/upload', {
        method: 'POST',
        body: formData,
      });
      const data = await response.json();
      setPdfText(data.text);  // Save the extracted text in state
    } catch (error) {
      console.error('Error:', error);
      setPdfText('');  // Reset the text on error
    }
  };

  const handleFileSelect = useCallback((event) => {
    event.preventDefault();
    const file = event.dataTransfer?.files[0] || event.target.files[0];
    if (file && file.type === 'application/pdf') {
      setFileName(file.name);
      handleFileUpload(file);
    } else {
      console.log('Please upload a PDF file.');
    }
  }, []);

  const onDragOver = (event) => {
    event.preventDefault();
  };

  return (
    <div>
      <div 
        className="drop-box"
        onDrop={handleFileSelect}
        onDragOver={onDragOver}
        style={{ /* your existing styles */ }}
      >
        Drag and drop a file here or click to select a file
        <input
          type="file"
          accept="application/pdf"
          style={{ /* your existing styles */ }}
          onChange={handleFileSelect}
          onClick={(event) => event.stopPropagation()}
        />
        {fileName && <p>File name: {fileName}</p>}
      </div>
      {/* Add a new div to display the PDF text */}
      {pdfText && (
        <div className="pdf-text-container">
          <h2>Extracted Text:</h2>
          <textarea 
            value={pdfText} 
            readOnly 
            style={{
              width: '100%',
              height: '300px',
              margin: '20px 0',
              padding: '10px',
              border: '1px solid #ddd',
              borderRadius: '4px',
            }}
          />
        </div>
      )}
    </div>
  );
};

export default Main;
