import React, { useCallback, useState } from 'react';

const Main = () => {
  const [fileName, setFileName] = useState('');

  const handleFileUpload = async (file) => {
    const formData = new FormData();
    formData.append('pdf', file);

    try {
      const response = await fetch('/upload', {
        method: 'POST',
        body: formData,
      });
      const data = await response.json();
      console.log(data.text); // The extracted text from the PDF
    } catch (error) {
      console.error('Error:', error);
    }
  };

  const handleFileSelect = useCallback((event) => {
    event.preventDefault();
    const file = event.dataTransfer?.files[0] || event.target.files[0];
    if (file && file.type === 'application/pdf') {
      setFileName(file.name);
      handleFileUpload(file);
    } else {
      console.log('Please upload a PDF file.');
    }
  }, []);

  const onDragOver = (event) => {
    event.preventDefault();
  };

  return (
    <div
      className="drop-box"
      onDrop={handleFileSelect}
      onDragOver={onDragOver}
      style={{
        width: '300px',
        height: '200px',
        borderWidth: '2px',
        borderColor: '#666',
        borderStyle: 'dashed',
        borderRadius: '5px',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        flexDirection: 'column',
        fontSize: '16px',
        color: '#666',
        marginBottom: '20px',
        position: 'relative',
      }}
    >
      Drag and drop a file here or click to select a file
      <input
        type="file"
        accept="application/pdf"
        style={{
          position: 'absolute',
          width: '100%',
          height: '100%',
          top: '0',
          left: '0',
          opacity: '0',
          cursor: 'pointer'
        }}
        onChange={handleFileSelect}
        onClick={(event) => event.stopPropagation()} // Prevent input from capturing clicks outside the drop-box
      />
      {fileName && <p>File name: {fileName}</p>}
    </div>
  );
};

export default Main;
