import React, { useCallback, useState } from 'react';
import { useDropzone } from 'react-dropzone';

const Main = () => {
  const [files, setFiles] = useState([]);

  const onDrop = useCallback(acceptedFiles => {
    setFiles(acceptedFiles.map(file => ({
      name: file.name,
      path: file.path
    })));
  }, []);

  const { getRootProps, getInputProps } = useDropzone({
    onDrop,
    accept: 'application/pdf'
  });

  return (
    <div style={styles.container}>
      <header style={styles.header}>
        <h1 style={styles.title}>AcquiSolar</h1>
        <a href="/about" style={styles.aboutLink}>About Us</a>
      </header>
      <main style={styles.main}>
        <div style={styles.leftPanel}>
          <div {...getRootProps({ style: styles.dropzone })}>
            <input {...getInputProps()} />
            <p style={styles.dropzoneText}>Just drag and drop your files. We'll do the rest.</p>
          </div>
          <ul style={styles.fileList}>
            {files.map(file => (
              <li key={file.path} style={styles.fileListItem}>{file.name}</li>
            ))}
          </ul>
        </div>
        <button style={styles.analyzeButton}>Analyze</button>
      </main>
    </div>
  );
};

const styles = {
  container: {
    fontFamily: 'Arial, sans-serif',
    height: '100vh',
    backgroundColor: '#9ACAC4',
    display: 'flex',
    flexDirection: 'column',
  },
  header: {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    padding: '20px',
    backgroundColor: '#9ACAC4',
    color: 'white',
    fontSize: '24px',
  },
  main: {
    flex: 1,
    display: 'flex',
    flexDirection: 'row',
    alignItems: 'flex-start',
    justifyContent: 'space-between',
    padding: '20px',
  },
  leftPanel: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'flex-start',
  },
  dropzone: {
    minWidth: '200px',
    height: '200px',
    borderWidth: '2px',
    borderColor: '#cccccc',
    borderStyle: 'dashed',
    backgroundColor: 'white',
    color: 'black',
    textAlign: 'center',
    padding: '20px',
    marginBottom: '10px',
  },
  fileList: {
    listStyleType: 'none',
    paddingLeft: '0',
    width: '200px',
    textAlign: 'left',
  },
  fileListItem: {
    borderBottom: '1px solid #cccccc',
    paddingBottom: '5px',
    marginBottom: '5px',
  },
  analyzeButton: {
    alignSelf: 'flex-end',
    padding: '10px 20px',
    fontSize: '16px',
    backgroundColor: '#4CAF50',
    border: 'none',
    borderRadius: '20px',
    cursor: 'pointer',
    color: 'white',
  },
  dropzoneText: {
    margin: 0,
  },
};

export default Main;
