// UI for the pdfviewer
import React from 'react';
import { Document, Page } from 'react-pdf';

const PDFViewer = ({ showSearch, currentPdf, numPages, searchQuery, handleSearchInputChange, handleSearchSubmit, onDocumentLoadSuccess, 
    instances, currentInstance, findInstancesOfSearchTerm, goToNextInstance, pageNumber, goToPrevousInstance }) => {
  return (
    <div style={styles.pdfViewer}>
      {showSearch && (
        <div style={styles.searchContainer}>
          <input
            type="text"
            placeholder="Search..."
            style={styles.searchInput}
            value={searchQuery}
            onChange={handleSearchInputChange}
          />
          <button style={styles.searchButton} onClick={handleSearchSubmit}>Search</button>
          {instances.length > 0 && (
        <button style={styles.searchButton} onClick={goToNextInstance}>
          Next Instance ({currentInstance + 1}/{instances.length})
        </button>
      )}
        </div>
      )}
      {showSearch && currentPdf && (
        <div style={styles.pdfContainer}>
          <Document
            file={currentPdf}
            onLoadSuccess={onDocumentLoadSuccess}
          >
            {/*Array.from(new Array(numPages), (el, index) => (
              <Page key={`page_${index + 1}`} pageNumber={index + 1} />
            ))*/}
            <Page pageNumber={pageNumber} />
          </Document>
        </div>
      )}
    </div>
  );
};

const styles = {
    searchContainer: {
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
    },
    searchInput: {
      height: '40px',
      fontSize: '18px',
      padding: '0 15px',
      border: '2px solid #9ACAC4',
      borderRadius: '20px',
      marginRight: '10px',
      outline: 'none',
    },
    searchButton: {
      padding: '10px 20px',
      fontSize: '16px',
      backgroundColor: '#FFF',
      color: 'black',
      border: 'none',
      borderRadius: '20px',
      cursor: 'pointer',
      outline: 'none',
      fontWeight: 'bold',
      margin: '4px'
    },
    pdfContainer: {
      maxHeight: '600px',
      overflowY: 'auto',
      marginLeft: '5%',
      marginRight: '10%',
    },
    pdfViewer: {
        flex: 1,
        flexDirection: 'column',
      }
  };

export default PDFViewer;
