import React, { useCallback, useState } from 'react';
import { useDropzone } from 'react-dropzone';

const Main = () => {
  const [files, setFiles] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');

  const onDrop = useCallback(acceptedFiles => {
    setFiles(prevFiles => [...prevFiles, ...acceptedFiles.map(file => file.name)]);
  }, []);

  const { getRootProps, getInputProps } = useDropzone({
    onDrop,
    accept: 'application/pdf'
  });

  const handleSearchChange = (event) => {
    setSearchTerm(event.target.value);
  };

  const handleSubmit = () => {
    // Submit action logic here
    console.log('Submit action!');
  };

  return (
    <div style={styles.container}>
      <header style={styles.header}>
        <h1 style={styles.title}>AcquiSolar</h1>
        <a href="/about" style={styles.aboutLink}>About Us</a>
      </header>
      <div style={styles.searchAndSubmitContainer}>
        <input
          type="text"
          placeholder="Search files..."
          value={searchTerm}
          onChange={handleSearchChange}
          style={styles.searchBar}
        />
        <button onClick={handleSubmit} style={styles.submitButton}>
          Submit
        </button>
      </div>
      <div {...getRootProps({ style: styles.dropzone })}>
        <input {...getInputProps()} />
        <p style={styles.uploadText}>Upload Files</p>
        <div style={styles.fileListContainer}>
          <p>Files:</p>
          <ul style={styles.fileList}>
            {files.map((fileName, index) => (
              <li key={index} style={styles.fileName}>{fileName}</li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
};

const styles = {
  container: {
    fontFamily: 'Arial, sans-serif',
    height: '100vh',
    backgroundColor: '#9ACAC4',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    padding: '20px',
  },
  header: {
    alignSelf: 'flex-start',
    width: '100%',
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    color: 'white',
  },
  title: {
    margin: 0,
  },
  aboutLink: {
    color: 'white',
    textDecoration: 'none',
  },
  searchAndSubmitContainer: {
    width: '100%',
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    padding: '0 20px',
    marginBottom: '20px',
  },
  searchBar: {
    flex: 1,
    padding: '10px',
    marginRight: '10px',
    borderRadius: '20px',
    border: '1px solid #ccc',
    outline: 'none',
  },
  submitButton: {
    padding: '10px 20px',
    fontSize: '16px',
    backgroundColor: '#4CAF50',
    border: 'none',
    borderRadius: '20px',
    cursor: 'pointer',
    color: 'white',
  },
  dropzone: {
    width: '100%',
    maxWidth: '500px',
    height: 'auto',
    borderWidth: '2px',
    borderRadius: '20px',
    borderColor: '#cccccc',
    borderStyle: 'dashed',
    backgroundColor: '#ffffff',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center',
    marginBottom: '10px',
  },
  uploadText: {
    color: '#000000',
    marginBottom: '20px',
    fontSize: '18px',
    fontWeight: 'bold',
  },
  fileListContainer: {
    width: '90%',
    backgroundColor: '#E6F0EF',
    borderRadius: '10px',
    padding: '10px',
    textAlign: 'left',
    maxHeight: '150px',
    overflowY: 'auto',
  },
  fileList: {
    listStyleType: 'none',
    padding: 0,
    margin: 0,
  },
  fileName: {
    color: '#000000',
    fontSize: '16px',
    lineHeight: '24px',
    padding: '5px 0',
  },
};

export default Main;
